import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import { Client } from 'discord.js';

type ContactPageProps = {
  isDark: boolean;
};

const ContactPage: React.FC<ContactPageProps> = ({ isDark }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<string[]>([]);
  const socket = io('http://localhost:4000'); // replace the URL with your server address

  const client = new Client();
  const channelID = 'YOUR_CHANNEL_ID'; // replace with your channel ID

  useEffect(() => {
    console.log('Connecting to Discord...');
    client.on('ready', () => {
      console.log(`Logged in as ${client.user?.tag}!`);
    });

    client.on('message', (discordMessage) => {
      setMessages((messages) => [
        ...messages,
        `[Discord] ${discordMessage.author.tag}: ${discordMessage.content}`,
      ]);
    });

    client.login('YOUR_BOT_TOKEN');

    return () => {
      console.log('Disconnecting from Discord...');
      client.destroy();
    };
  }, []);

  const handleMessageSend = async (msg: string) => {
    try {
      const channel = client.channels.cache.get(channelID);
      if (!channel) return console.error('Invalid channel!');
      await channel.send(msg);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    if (message.trim() !== '') {
      socket.emit('message', message);
      handleMessageSend(message);
      setMessage('');
    }
  };

  return (
    <div>
      <div
        className="main2"
        style={{
          backgroundImage: isDark
            ? 'radial-gradient(#ffffff 1px, #000000 1px)'
            : 'radial-gradient(#000000 1px, #ffffff 1px)',
        }}
      >
        <table
          className="contact_form"
          style={{
            backgroundColor: isDark ? '#000' : '#fff',
            border: isDark ? '1px solid white' : '1px solid black',
          }}
        >
          <tbody>
            <tr>
              <td>
                <br />
                <input
                  style={{
                    backgroundColor: isDark ? '#000' : '#fff',
                    color: isDark ? '#fff' : '#000',
                    borderBottom: isDark ? '1px solid white' : '1px solid black',
                  }}
                  type="text"
                  placeholder="Name"
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  style={{
                    backgroundColor: isDark ? '#000' : '#fff',
                    color: isDark ? '#fff' : '#000',
                    borderBottom: isDark ? '1px solid white' : '1px solid black',
                  }}
                  type="email"
                  placeholder="Email"
                />
              </td>
            </tr>
            <tr>
              <td>
                <textarea
                  style={{
                    backgroundColor: isDark ? '#000' : '#fff',
                    color: isDark ? '#fff' : '#000',
                    borderBottom: isDark ? '1px solid white' : '1px solid black',
                  }}
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Message"
                ></textarea>
              </td>
            </tr>
            <tr>
              <td>
                <button
                  onClick={handleSubmit}
                  style={{
                    backgroundColor: isDark ? '#fff' : '#000',
                    color: isDark ? '#000' : '#fff',
                  }}
                >
                  Contact Us
                </button>
                <br />
              </td>
            </tr>
            {messages.map((message, index) => (
              <tr key={index}>
                <td>{message}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ContactPage;
