import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import io, { Socket } from 'socket.io-client';

type ContactPageProps = {
  isDark: boolean;
};

const ContactPage: React.FC<ContactPageProps> = ({ isDark }) => {
  const [socket, setSocket] = useState<Socket | undefined>(undefined);
  const [message, setMessage] = useState<string>('');

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const message = e.currentTarget.elements.namedItem('message').value;

    // Emit a 'contact' event to the Socket.IO server with the message data
    socket?.emit('contact', message, (response: string) => {
      console.log(response);
    });
  };

  if (!socket) {
    // Connect to the Socket.IO server after the first render
    const newSocket = io('http://18.200.219.78:80/');
    setSocket(newSocket);

    // Listen for incoming 'message_received' events from the server
    newSocket.on('message_received', (message: string) => {
      setMessage(message);
    });
  }

  return (
    <div className="main2" style={{ backgroundImage: isDark ? 'radial-gradient(#ffffff 1px, #000000 1px)' : 'radial-gradient(#000000 1px, #ffffff 1px)' }}>
      <form onSubmit={handleSubmit} className="contact_form" style={{ backgroundColor: isDark ? '#000' : '#fff', border: isDark ? '1px solid white' : '1px solid black' }}>
        <textarea style={{ backgroundColor: isDark ? '#000' : '#fff', color: isDark ? '#fff' : '#000', borderBottom: isDark ? '1px solid white' : '1px solid black' }} name="message" placeholder="Message"></textarea>
        <button style={{ backgroundColor: isDark ? '#fff' : '#000', color: isDark ? '#000' : '#fff' }}>Contact Us</button>
      </form>
      {message && <div>{message}</div>}
    </div>
  );
};

export default ContactPage;
