import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

type ContactPageProps = {
  isDark: boolean;
};

const ContactPage: React.FC<ContactPageProps> = ({ isDark }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState<string[]>([]);
  const socket = io('http://localhost:4000'); // replace the URL with your server address

  useEffect(() => {
    socket.on('message', (data: string) => {
      setMessages((messages) => [...messages, data].reverse());
    });
  }, [socket]);

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    if (message.trim() !== '') {
      socket.emit('message', message);
      setMessage('');
    }
  };

  return (
    <div>
      <div className='main2' style={{ backgroundImage: isDark ? 'radial-gradient(#ffffff 1px, #000000 1px)' 
        :
        'radial-gradient(#000000 1px, #ffffff 1px)' }}>
        <h2 style={{ color: isDark ? '#fff' : '#000' }}>Chat with us</h2>
        <form onSubmit={handleSubmit}>
          <table className='contact_form' style={{ backgroundColor: isDark ? '#000' : '#fff', border: isDark ? '1px solid white' : '1px solid black' }}>
            <tbody>
              <tr>
                <td><textarea name="chat" style={{ backgroundColor: isDark ? '#000' : '#fff', color: isDark ? '#fff' : '#000', borderBottom: isDark ? '1px solid white' : '1px solid black' }} value={message} onChange={e => setMessage(e.target.value)} placeholder="Type your message here..."></textarea></td>
              </tr>
              <tr>
                <td><button type="submit" style={{ backgroundColor: isDark ? '#fff' : '#000', color: isDark ? '#000' : '#fff' }}>Send</button><br /></td>
              </tr>
              {messages.map((message, index) => (
                <tr key={index}>
                  <td style={{ textAlign: message.startsWith('You: ') ? 'right' : 'left' }}>
                    <div style={{ backgroundColor: message.startsWith('You: ') ? 'lightblue' : '#f1f1f1', padding: '10px', borderRadius: '20px', display: 'inline-block', marginBottom: '10px', maxWidth: '70%' }}>
                      {message}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <p style={{ color: isDark ? '#fff' : '#000' }}>Type a message and press enter to send. Press the send button or hit enter on an empty message to start a new chat.</p>
        </form>
      </div>
    </div>
  );
};

export default ContactPage;
