import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

type ContactPageProps = {
  isDark: boolean;
};

const ContactPage: React.FC<ContactPageProps> = ({ isDark }) => {
  const [socket, setSocket] = useState<SocketIOClient.Socket | null>(null);
  const [response, setResponse] = useState<string>('');

  useEffect(() => {
    // Connect to the Socket.IO server
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    // Disconnect from the Socket.IO server when the component unmounts
    return () => newSocket.disconnect();
  }, []);

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const name = e.currentTarget?.elements?.namedItem('name')?.value as string;
    const email = e.currentTarget?.elements?.namedItem('email')?.value as string;
    const message = e.currentTarget?.elements?.namedItem('message')?.value as string;

    if (socket) {
      // Emit a 'contact' event to the Socket.IO server with the message data
      socket.emit('contact', { name, email, message }, (response: string) => {
        setResponse(response);
      });
    }
  };

  return (
    <div className="main2" style={{ backgroundImage: isDark ? 'radial-gradient(#ffffff 1px, #000000 1px)' : 'radial-gradient(#000000 1px, #ffffff 1px)' }}>
      <form onSubmit={handleSubmit} className="contact_form" style={{ backgroundColor: isDark ? '#000' : '#fff', border: isDark ? '1px solid white' : '1px solid black' }}>
        <input style={{ backgroundColor: isDark ? '#000' : '#fff', color: isDark ? '#fff' : '#000', borderBottom: isDark ? '1px solid white' : '1px solid black' }} type="text" name="name" placeholder="Name" />
        <input style={{ backgroundColor: isDark ? '#000' : '#fff', color: isDark ? '#fff' : '#000', borderBottom: isDark ? '1px solid white' : '1px solid black' }} type="email" name="email" placeholder="Email" />
        <textarea style={{ backgroundColor: isDark ? '#000' : '#fff', color: isDark ? '#fff' : '#000', borderBottom: isDark ? '1px solid white' : '1px solid black' }} name="message" placeholder="Message"></textarea>
        <button style={{ backgroundColor: isDark ? '#fff' : '#000', color: isDark ? '#000' : '#fff' }}>Contact Us</button>
      </form>
      {response && <div>{response}</div>}
    </div>
  );
};

export default ContactPage;
